var canvas,ctx,x=75,y=50,WIDTH=1e4,HEIGHT=3e3,dragok=!1;function rect(a,e,t,n){ctx.beginPath(),ctx.rect(a,e,t,n),ctx.closePath(),ctx.fill()}function clear(){ctx.clearRect(0,0,WIDTH,HEIGHT)}function init(){return canvas=document.getElementById("canvas"),ctx=canvas.getContext("2d"),setInterval(draw,10)}function draw(){clear(),ctx.fillStyle="#FAF7F8",rect(0,0,WIDTH,HEIGHT),ctx.fillStyle="#444444",rect(x-15,y-15,30,30)}function myMove(a){dragok&&(x=a.pageX-canvas.offsetLeft,y=a.pageY-canvas.offsetTop)}function myDown(a){a.pageX<x+15+canvas.offsetLeft&&a.pageX>x-15+canvas.offsetLeft&&a.pageY<y+15+canvas.offsetTop&&a.pageY>y-15+canvas.offsetTop&&(x=a.pageX-canvas.offsetLeft,y=a.pageY-canvas.offsetTop,dragok=!0,canvas.onmousemove=myMove)}function myUp(){dragok=!1,canvas.onmousemove=null}init(),canvas.onmousedown=myDown,canvas.onmouseup=myUp;